import { useEffect, useState } from "react";
import { Row, Col, Container } from "react-bootstrap";
import { ArrowRightCircle } from "react-bootstrap-icons";
import headerImg from "../assets/img/logo.svg";

export const Banner = () => {
  const [loopNum, setLoopNum] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const [text, setText] = useState("");
  const [delta, setDelta] = useState(100 - Math.random() * 100);
  const toRotate = ["FullStack Developer", "/", " ", "/", " ", "Web Designer", "UX/UI Designer"];
  const period = 1000;

  useEffect(() => {
    let ticker = setInterval(() => {
      tick();
    }, delta);

    return () => {
      clearInterval(ticker);
    };
  }, [text]);

  const tick = () => {
    let i = loopNum % toRotate.length;
    let fullText = toRotate[i];
    let updatedText = isDeleting
      ? fullText.substring(0, text.length - 1)
      : fullText.substring(0, text.length + 1);

    setText(updatedText);

    if (isDeleting) {
      setDelta((prevDelta) => prevDelta / 1.4);
    }

    if (!isDeleting && updatedText === fullText) {
      setIsDeleting(true);
      setDelta(period);
    } else if (isDeleting && updatedText === "") {
      setIsDeleting(false);
      setLoopNum(loopNum + 1);
      setDelta(500);
    }
  };

  return (
    <section className="banner" id="home">
      <Container className="banner-texts">
        <span className="tagline">Welcome to my Portfolio</span>
        <h1>
          <span className="wrap">{text}</span>
        </h1>
        <p>Lorem Ipsum blablablablablablabla</p>
        <button onClick={() => console.log("connect")}>
          Let's Connect <ArrowRightCircle size={25} />
        </button>
      </Container>
      <div className="banner-animated-icon">
        <div className="banner-img-updown">
          <div className="banner-img-leftright">
            <img src={headerImg} alt="header img" draggable="false" />
          </div>
        </div>
      </div>
    </section>
  );
};

export default Banner;
